import { useState } from "react";
import { useNavigate } from "react-router-dom"; // بديل useRouter من Next
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "../components/ui/card";
import { Label } from "../components/ui/label";
import { RadioGroup, RadioGroupItem } from "../components/ui/radio-group";
import { Link } from "react-router-dom"; // بديل Link من Next
import { Clock } from "../components/clock";
import axios from "axios";

export default function SignUp() {
  const navigate = useNavigate(); // بديل useRouter
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    role: "student",
    studentId: "",
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleRoleChange = (value) => {
    setFormData({ ...formData, role: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    if (formData.role === "student" && !/^\d{8}$/.test(formData.studentId)) {
      setError("Student ID must be exactly 8 digits");
      return;
    }

    try {
      await axios.post("/signup", { // يجب تحديد URL كامل
        name: formData.name,
        email: formData.email,
        password: formData.password,
        role: formData.role,
        studentId: formData.role === "student" ? formData.studentId : undefined,
      });

      navigate("/signin"); // بديل router.push
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-900 text-white">
      <header className="p-4 border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Task Management System</h1>
          <Clock />
        </div>
      </header>

      <main className="flex-grow flex items-center justify-center p-4">
        <Card className="w-full max-w-md bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-2xl text-center">Sign Up</CardTitle>
          </CardHeader>
          <CardContent>
            {error && <div className="bg-red-900/50 text-red-200 p-3 rounded-md mb-4">{error}</div>}
            <form onSubmit={handleSubmit} className="space-y-4">
              <Input name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} required />
              <Input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
              <Input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
              <Input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} required />

              <div className="space-y-2">
                <Label>Role</Label>
                <RadioGroup value={formData.role} onValueChange={handleRoleChange} className="flex space-x-4">
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="student" id="student" />
                    <Label htmlFor="student">Student</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="admin" id="admin" />
                    <Label htmlFor="admin">Administrator</Label>
                  </div>
                </RadioGroup>
              </div>

              {formData.role === "student" && (
                <Input name="studentId" placeholder="Student ID" value={formData.studentId} onChange={handleChange} required />
              )}

              <Button type="submit" className="w-full">Sign Up</Button>

              <div className="text-center mt-4">
                <p>Already have an account? <Link to="/signin" className="text-blue-400 hover:underline">Sign In</Link></p>
              </div>
            </form>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}
