import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import DashboardLayout from '../components/dashboard-layout';
import { Clock } from 'lucide-react';
import Chart from 'chart.js/auto';
import api from '../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const { user: currentUser } = useAuth();
  const [users, setUsers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [stats, setStats] = useState({
    projectsCount: 0,
    studentsCount: 0,
    tasksCount: 0,
    finishedProjectsCount: 0,
  });

  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      // Fetch data from API
      const [usersResponse, tasksResponse, projectsResponse] = await Promise.all([
        api.get('/users'),
        api.get('/tasks'),
        api.get('/projects'),
      ]);

      // Ensure we have arrays, even if empty
      const usersData = Array.isArray(usersResponse.data) ? usersResponse.data : [];
      const tasksData = Array.isArray(tasksResponse.data) ? tasksResponse.data : [];
      const projectsData = Array.isArray(projectsResponse.data) ? projectsResponse.data : [];

      // Calculate stats
      const studentsCount = usersData.filter(user => user.role === 'student').length;
      const tasksCount = tasksData.length;
      const projectsCount = projectsData.length;
      const finishedProjectsCount = projectsData.filter(project => project.status === 'completed').length;

      setStats({
        projectsCount,
        studentsCount,
        tasksCount,
        finishedProjectsCount,
      });

      // Initialize chart
      initializeChart(projectsCount, studentsCount, tasksCount, finishedProjectsCount);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const initializeChart = (projectsCount, studentsCount, tasksCount, finishedProjectsCount) => {
    if (chartRef.current) {
      // Destroy existing chart if it exists
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }

      const ctx = chartRef.current.getContext('2d');
      if (ctx) {
        chartInstance.current = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: ['Projects', 'Students', 'Tasks', 'Finished Projects'],
            datasets: [
              {
                label: 'Count',
                data: [projectsCount, studentsCount, tasksCount, finishedProjectsCount],
                backgroundColor: [
                  'rgba(0, 102, 102, 0.7)',
                  'rgba(51, 102, 153, 0.7)',
                  'rgba(102, 102, 0, 0.7)',
                  'rgba(102, 51, 0, 0.7)',
                ],
                borderColor: [
                  'rgba(0, 102, 102, 1)',
                  'rgba(51, 102, 153, 1)',
                  'rgba(102, 102, 0, 1)',
                  'rgba(102, 51, 0, 1)',
                ],
                borderWidth: 1,
              },
            ],
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
              x: {
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
            },
            plugins: {
              legend: {
                display: false,
              },
              title: {
                display: true,
                text: 'Admin Dashboard Overview',
                color: 'rgba(255, 255, 255, 0.7)',
                font: {
                  size: 16,
                },
              },
            },
          },
        });
      }
    }
  };

  return (
    <DashboardLayout title="Admin Dashboard" userRole="admin">
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-blue-500">
            Welcome to the Task Management System
          </h1>
          <Clock className="w-6 h-6 text-gray-400" />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="bg-gray-800 border border-gray-700 rounded-lg shadow-lg">
            <div className="p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-300 mb-2">Number of Projects</h3>
              <p className="text-4xl font-bold text-white">{stats.projectsCount}</p>
            </div>
          </div>

          <div className="bg-gray-800 border border-gray-700 rounded-lg shadow-lg">
            <div className="p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-300 mb-2">Number of Students</h3>
              <p className="text-4xl font-bold text-white">{stats.studentsCount}</p>
            </div>
          </div>

          <div className="bg-gray-800 border border-gray-700 rounded-lg shadow-lg">
            <div className="p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-300 mb-2">Number of Tasks</h3>
              <p className="text-4xl font-bold text-white">{stats.tasksCount}</p>
            </div>
          </div>

          <div className="bg-gray-800 border border-gray-700 rounded-lg shadow-lg">
            <div className="p-6 text-center">
              <h3 className="text-lg font-semibold text-gray-300 mb-2">Finished Projects</h3>
              <p className="text-4xl font-bold text-white">{stats.finishedProjectsCount}</p>
            </div>
          </div>
        </div>

        <div className="w-full h-96 bg-gray-800 border border-gray-700 rounded-lg p-4">
          <canvas ref={chartRef} className="w-full h-full"></canvas>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Dashboard;
