import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import Sidebar from '../components/sidebar';
import axios from 'axios';

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/projects', {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        setProjects(response.data.projects);
      } catch (error) {
        console.error('خطأ في جلب المشاريع:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  // تنسيق التاريخ
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-SA', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  // حساب نسبة الإكمال
  const calculateProgress = (project) => {
    if (!project.tasks || project.tasks.length === 0) return 0;

    const completedTasks = project.tasks.filter(task => task.status === 'completed').length;
    return Math.round((completedTasks / project.tasks.length) * 100);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* الشريط الجانبي */}
      <Sidebar />

      {/* المحتوى الرئيسي */}
      <div className="flex-1 overflow-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-semibold text-gray-800">المشاريع</h1>
            <button
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors"
            >
              إنشاء مشروع جديد
            </button>
          </div>

          {loading ? (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
          ) : projects.length === 0 ? (
            <div className="bg-white rounded-lg shadow-sm p-6 text-center text-gray-500">
              لا توجد مشاريع متاحة
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {projects.map((project) => (
                <div key={project.id} className="bg-white rounded-lg shadow-sm overflow-hidden">
                  <div className="p-6">
                    <h2 className="text-xl font-semibold text-gray-800 mb-2">{project.name}</h2>
                    <p className="text-gray-600 mb-4">{project.description}</p>

                    <div className="flex justify-between text-sm text-gray-500 mb-4">
                      <span>تاريخ البدء: {formatDate(project.startDate)}</span>
                      <span>تاريخ الانتهاء: {formatDate(project.endDate)}</span>
                    </div>

                    <div className="mb-2">
                      <div className="flex justify-between text-sm mb-1">
                        <span>التقدم</span>
                        <span>{calculateProgress(project)}%</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2.5">
                        <div
                          className="bg-blue-600 h-2.5 rounded-full"
                          style={{ width: `${calculateProgress(project)}%` }}
                        ></div>
                      </div>
                    </div>

                    <div className="flex justify-between items-center mt-4">
                      <div className="flex -space-x-2 rtl:space-x-reverse">
                        {project.team && project.team.slice(0, 3).map((member, index) => (
                          <div
                            key={index}
                            className="w-8 h-8 rounded-full bg-blue-500 border-2 border-white flex items-center justify-center text-white text-xs font-semibold"
                            title={member.name}
                          >
                            {member.name.charAt(0).toUpperCase()}
                          </div>
                        ))}
                        {project.team && project.team.length > 3 && (
                          <div className="w-8 h-8 rounded-full bg-gray-300 border-2 border-white flex items-center justify-center text-gray-600 text-xs font-semibold">
                            +{project.team.length - 3}
                          </div>
                        )}
                      </div>

                      <span className={`px-2 py-1 text-xs rounded-full ${
                        project.status === 'completed' ? 'bg-green-100 text-green-800' : 
                        project.status === 'in_progress' ? 'bg-blue-100 text-blue-800' : 
                        'bg-yellow-100 text-yellow-800'
                      }`}>
                        {project.status === 'completed' ? 'مكتمل' : 
                         project.status === 'in_progress' ? 'قيد التنفيذ' : 
                         'معلق'}
                      </span>
                    </div>
                  </div>

                  <div className="border-t px-6 py-3 bg-gray-50 flex justify-end">
                    <button className="text-blue-600 hover:text-blue-800 text-sm font-medium">
                      عرض التفاصيل
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectsPage;
