import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const SignUpPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'user', // القيمة الافتراضية
    studentId: '' // فقط للطلاب
  });

  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('');

    // التحقق من تطابق كلمات المرور
    if (formData.password !== formData.confirmPassword) {
      setErrorMessage('كلمات المرور غير متطابقة');
      setIsLoading(false);
      return;
    }

    try {
      // إرسال بيانات التسجيل بدون confirmPassword
      const { confirmPassword, ...registrationData } = formData;

      const success = await register(registrationData);

      if (success) {
        // إعادة توجيه المستخدم بناءً على دوره
        const user = JSON.parse(sessionStorage.getItem('user'));
        if (user.role === 'admin') {
          navigate('/dashboard');
        } else if (user.role === 'student') {
          navigate('/student-dashboard');
        } else {
          navigate('/dashboard');
        }
      } else {
        setErrorMessage('فشل إنشاء الحساب. يرجى المحاولة مرة أخرى.');
      }
    } catch (error) {
      console.error('خطأ في التسجيل:', error);
      setErrorMessage('حدث خطأ أثناء محاولة إنشاء الحساب. يرجى المحاولة مرة أخرى.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          إنشاء حساب جديد
        </h2>
        <p className="mt-2 text-center text-sm text-gray-600">
          أو{' '}
          <Link to="/signin" className="font-medium text-blue-600 hover:text-blue-500">
            تسجيل الدخول إلى حسابك
          </Link>
        </p>
      </div>

