import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import DashboardLayout from '../components/dashboard-layout';
import { CheckCircle } from 'lucide-react';
import Chart from 'chart.js/auto';
import api from '../services/api';

const StudentDashboard = () => {
  const navigate = useNavigate();
  const { user: currentUser } = useAuth();
  const [users, setUsers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [studentProjects, setStudentProjects] = useState([]);
  const [taskFilters, setTaskFilters] = useState({
    status: [],
    assigneeId: '',
    startDate: null,
    endDate: null,
    searchTerm: '',
    sortBy: 'createdAt',
    sortOrder: 'desc',
  });

  // Chart refs
  const statusChartRef = useRef(null);
  const projectsChartRef = useRef(null);
  const timelineChartRef = useRef(null);
  const statusChartInstance = useRef(null);
  const projectsChartInstance = useRef(null);
  const timelineChartInstance = useRef(null);

  useEffect(() => {
    if (!currentUser) {
      navigate('/signin');
      return;
    }

    if (currentUser.role !== 'student') {
      navigate('/dashboard');
      return;
    }

    fetchUsers();
    fetchTasks();
    fetchProjects();
  }, [currentUser, navigate]);

  useEffect(() => {
    if (!currentUser) return;

    // First filter tasks assigned to this student
    const studentTasks = tasks.filter((task) => task.assignedTo === currentUser.id);

    // Then apply additional filters
    let result = [...studentTasks];

    // Filter by status
    if (taskFilters.status.length > 0) {
      result = result.filter((task) => taskFilters.status.includes(task.status));
    }

    // Filter by date range
    if (taskFilters.startDate) {
      result = result.filter((task) => new Date(task.createdAt) >= taskFilters.startDate);
    }
    if (taskFilters.endDate) {
      const endDate = new Date(taskFilters.endDate);
      endDate.setDate(endDate.getDate() + 1);
      result = result.filter((task) => new Date(task.createdAt) <= endDate);
    }

    // Filter by search term
    if (taskFilters.searchTerm) {
      const searchLower = taskFilters.searchTerm.toLowerCase();
      result = result.filter(
        (task) =>
          task.title.toLowerCase().includes(searchLower) || task.description.toLowerCase().includes(searchLower),
      );
    }

    // Sort results
    result.sort((a, b) => {
      let valueA, valueB;

      if (taskFilters.sortBy === 'createdAt') {
        valueA = new Date(a.createdAt).getTime();
        valueB = new Date(b.createdAt).getTime();
      } else if (taskFilters.sortBy === 'title') {
        valueA = a.title.toLowerCase();
        valueB = b.title.toLowerCase();
      } else if (taskFilters.sortBy === 'status') {
        valueA = a.status;
        valueB = b.status;
      } else {
        valueA = a[taskFilters.sortBy];
        valueB = b[taskFilters.sortBy];
      }

      return taskFilters.sortOrder === 'asc'
        ? valueA > valueB ? 1 : valueA < valueB ? -1 : 0
        : valueA < valueB ? 1 : valueA > valueB ? -1 : 0;
    });

    setFilteredTasks(result);

    // Initialize charts
    initializeCharts(studentTasks, studentProjects);
  }, [tasks, taskFilters, currentUser, studentProjects]);

  useEffect(() => {
    fetchProjects();
  }, [fetchProjects]);

  useEffect(() => {
    initializeCharts();
  }, [initializeCharts]);

  const fetchUsers = async () => {
    try {
      const response = await api.get('/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await api.get('/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const fetchProjects = async () => {
    try {
      const response = await api.get('/projects');
      const allProjects = response.data;
      setProjects(allProjects);

      const userProjects = allProjects.filter((project) => project.students.includes(currentUser.id));
      setStudentProjects(userProjects);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const initializeCharts = (studentTasks, studentProjects) => {
    initializeStatusChart(studentTasks);
    initializeProjectsChart(studentProjects);
    initializeTimelineChart(studentTasks);
  };

  const initializeStatusChart = (studentTasks) => {
    if (statusChartRef.current) {
      if (statusChartInstance.current) {
        statusChartInstance.current.destroy();
      }

      const pendingCount = studentTasks.filter((task) => task.status === 'pending').length;
      const inProgressCount = studentTasks.filter((task) => task.status === 'in-progress').length;
      const completedCount = studentTasks.filter((task) => task.status === 'completed').length;

      const ctx = statusChartRef.current.getContext('2d');
      if (ctx) {
        statusChartInstance.current = new Chart(ctx, {
          type: 'doughnut',
          data: {
            labels: ['Pending', 'In Progress', 'Completed'],
            datasets: [
              {
                data: [pendingCount, inProgressCount, completedCount],
                backgroundColor: ['rgba(255, 193, 7, 0.7)', 'rgba(13, 110, 253, 0.7)', 'rgba(40, 167, 69, 0.7)'],
                borderColor: ['rgba(255, 193, 7, 1)', 'rgba(13, 110, 253, 1)', 'rgba(40, 167, 69, 1)'],
                borderWidth: 1,
              },
            ],
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                position: 'right',
                labels: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
              },
              title: {
                display: true,
                text: 'Task Status Distribution',
                color: 'rgba(255, 255, 255, 0.9)',
                font: {
                  size: 16,
                },
              },
            },
          },
        });
      }
    }
  };

  const initializeProjectsChart = (studentProjects) => {
    if (projectsChartRef.current && studentProjects.length > 0) {
      if (projectsChartInstance.current) {
        projectsChartInstance.current.destroy();
      }

      const ctx = projectsChartRef.current.getContext('2d');
      if (ctx) {
        projectsChartInstance.current = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: studentProjects.map((project) => project.title),
            datasets: [
              {
                label: 'Project Progress (%)',
                data: studentProjects.map((project) => project.progress),
                backgroundColor: studentProjects.map((project) => {
                  if (project.status === 'completed') return 'rgba(40, 167, 69, 0.7)';
                  if (project.status === 'in-progress') return 'rgba(13, 110, 253, 0.7)';
                  return 'rgba(255, 193, 7, 0.7)';
                }),
                borderColor: studentProjects.map((project) => {
                  if (project.status === 'completed') return 'rgba(40, 167, 69, 1)';
                  if (project.status === 'in-progress') return 'rgba(13, 110, 253, 1)';
                  return 'rgba(255, 193, 7, 1)';
                }),
                borderWidth: 1,
              },
            ],
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                max: 100,
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
              x: {
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                  callback: function (value) {
                    const label = this.getLabelForValue(value);
                    if (label.length > 15) {
                      return label.substring(0, 15) + '...';
                    }
                    return label;
                  },
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
            },
            plugins: {
              legend: {
                display: false,
              },
              title: {
                display: true,
                text: 'My Projects Progress',
                color: 'rgba(255, 255, 255, 0.9)',
                font: {
                  size: 16,
                },
              },
              tooltip: {
                callbacks: {
                  label: (context) => {
                    const project = studentProjects[context.dataIndex];
                    return [`Progress: ${context.parsed.y}%`, `Status: ${project.status}`, `Due: ${project.endDate}`];
                  },
                },
              },
            },
          },
        });
      }
    }
  };

  const initializeTimelineChart = (studentTasks) => {
    if (timelineChartRef.current) {
      if (timelineChartInstance.current) {
        timelineChartInstance.current.destroy();
      }

      const tasksByMonth = {};
      const completedByMonth = {};

      const months = [];
      const now = new Date();
      for (let i = 5; i >= 0; i--) {
        const month = new Date(now.getFullYear(), now.getMonth() - i, 1);
        const monthKey = `${month.getFullYear()}-${month.getMonth() + 1}`;
        months.push(monthKey);
        tasksByMonth[monthKey] = 0;
        completedByMonth[monthKey] = 0;
      }

      studentTasks.forEach((task) => {
        const taskDate = new Date(task.createdAt);
        const monthKey = `${taskDate.getFullYear()}-${taskDate.getMonth() + 1}`;

        if (tasksByMonth[monthKey] !== undefined) {
          tasksByMonth[monthKey]++;
          if (task.status === 'completed') {
            completedByMonth[monthKey]++;
          }
        }
      });

      const ctx = timelineChartRef.current.getContext('2d');
      if (ctx) {
        timelineChartInstance.current = new Chart(ctx, {
          type: 'line',
          data: {
            labels: months.map((month) => {
              const [year, monthNum] = month.split('-');
              return `${new Date(parseInt(year), parseInt(monthNum) - 1).toLocaleString('default', {
                month: 'short',
              })} ${year}`;
            }),
            datasets: [
              {
                label: 'Total Tasks',
                data: months.map((month) => tasksByMonth[month]),
                borderColor: 'rgba(54, 162, 235, 1)',
                backgroundColor: 'rgba(54, 162, 235, 0.2)',
                tension: 0.3,
                fill: true,
              },
              {
                label: 'Completed Tasks',
                data: months.map((month) => completedByMonth[month]),
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                tension: 0.3,
                fill: true,
              },
            ],
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                  precision: 0,
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
              x: {
                ticks: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
                grid: {
                  color: 'rgba(255, 255, 255, 0.1)',
                },
              },
            },
            plugins: {
              legend: {
                labels: {
                  color: 'rgba(255, 255, 255, 0.7)',
                },
              },
              title: {
                display: true,
                text: 'Task Timeline (Last 6 Months)',
                color: 'rgba(255, 255, 255, 0.9)',
                font: {
                  size: 16,
                },
              },
            },
          },
        });
      }
    }
  };

  const handleUpdateTaskStatus = async (taskId, status) => {
    try {
      await api.put(`/tasks/${taskId}`, { status });
      const updatedTasks = tasks.map((task) => (task.id === taskId ? { ...task, status } : task));
      setTasks(updatedTasks);
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  const getAdminName = (id) => {
    const admin = users.find((user) => user.id === id);
    return admin ? admin.name : 'Unknown Admin';
  };

  const getStatusBadge = (status) => {
    const badgeColors = {
      pending: 'bg-yellow-900/20 text-yellow-400 border-yellow-500',
      'in-progress': 'bg-blue-900/20 text-blue-400 border-blue-500',
      completed: 'bg-green-900/20 text-green-400 border-green-500',
    };

    return (
      <span className={`px-2 py-1 rounded-full border text-sm ${badgeColors[status]}`}>
        {status.replace('-', ' ')}
      </span>
    );
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  };

  return (
    <DashboardLayout title="Student Dashboard" userRole="student">
      <div className="space-y-6">
        <div className="mb-6">
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-xl font-semibold mb-2 text-foreground">
                Welcome, {currentUser?.name}
              </h2>
              {currentUser?.studentId && (
                <p className="text-muted-foreground">Student ID: {currentUser.studentId}</p>
              )}
            </div>
          </div>
        </div>

        <div className="dashboard-stats">
          <div className="card-dashboard">
            <div className="p-4">
              <h3 className="text-lg font-semibold text-foreground mb-4">Task Status</h3>
              <div className="h-64">
                <canvas ref={statusChartRef} className="w-full h-full"></canvas>
              </div>
            </div>
          </div>

          <div className="card-dashboard">
            <div className="p-4">
              <h3 className="text-lg font-semibold text-foreground mb-4">My Projects</h3>
              <div className="h-64">
                {studentProjects.length > 0 ? (
                  <canvas ref={projectsChartRef} className="w-full h-full"></canvas>
                ) : (
                  <div className="flex items-center justify-center h-full text-muted-foreground">
                    <p>No projects assigned yet</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="card-dashboard">
            <div className="p-4">
              <h3 className="text-lg font-semibold text-foreground mb-4">Task Summary</h3>
              <div className="space-y-4">
                <div className="grid grid-cols-3 gap-4">
                  <div className="bg-secondary p-4 rounded-lg text-center">
                    <h3 className="text-yellow-400 text-lg font-bold">Pending</h3>
                    <p className="text-3xl font-bold text-foreground">
                      {tasks.filter((task) => task.assignedTo === currentUser?.id && task.status === 'pending').length}
                    </p>
                  </div>
                  <div className="bg-secondary p-4 rounded-lg text-center">
                    <h3 className="text-blue-400 text-lg font-bold">In Progress</h3>
                    <p className="text-3xl font-bold text-foreground">
                      {tasks.filter((task) => task.assignedTo === currentUser?.id && task.status === 'in-progress').length}
                    </p>
                  </div>
                  <div className="bg-secondary p-4 rounded-lg text-center">
                    <h3 className="text-green-400 text-lg font-bold">Completed</h3>
                    <p className="text-3xl font-bold text-foreground">
                      {tasks.filter((task) => task.assignedTo === currentUser?.id && task.status === 'completed').length}
                    </p>
                  </div>
                </div>

                <div className="bg-secondary p-4 rounded-lg">
                  <h3 className="text-lg font-bold mb-2 text-foreground">Projects Overview</h3>
                  <p className="text-muted-foreground">
                    {studentProjects.length > 0
                      ? `You are assigned to ${studentProjects.length} project${
                          studentProjects.length > 1 ? 's' : ''
                        }.`
                      : 'No projects assigned yet.'}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="card-dashboard">
          <div className="p-4">
            <h3 className="text-lg font-semibold text-foreground mb-4">Task Timeline</h3>
            <div className="h-64">
              <canvas ref={timelineChartRef} className="w-full h-full"></canvas>
            </div>
          </div>
        </div>

        <h2 className="text-2xl font-bold text-foreground">My Tasks</h2>

        {filteredTasks.length === 0 ? (
          <div className="card-dashboard">
            <div className="p-6">
              <p className="text-center text-muted-foreground">You don't have any tasks matching your filters.</p>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredTasks.map((task) => (
              <div key={task.id} className="card-dashboard">
                <div className="p-4 pb-2">
                  <div className="flex justify-between items-start">
                    <h3 className="text-lg font-semibold text-foreground">{task.title}</h3>
                    {getStatusBadge(task.status)}
                  </div>
                </div>
                <div className="p-4">
                  <div className="space-y-4">
                    <p className="text-sm text-muted-foreground">{task.description}</p>
                    {task.hasProblem && (
                      <div className="bg-destructive/20 border border-destructive rounded-md p-2 text-sm">
                        <p className="font-semibold text-destructive-foreground">Problem:</p>
                        <p className="text-muted-foreground">{task.problemDescription}</p>
                      </div>
                    )}
                    <div className="text-xs text-muted-foreground">
                      <p>Created: {formatDate(task.createdAt)}</p>
                      <p>Created by: {getAdminName(task.createdBy)}</p>
                      {task.projectId && (
                        <p>
                          Project: {projects.find((p) => p.id === task.projectId)?.title || 'Unknown Project'}
                        </p>
                      )}
                    </div>
                    <div className="flex space-x-2 pt-2">
                      <button
                        className={`px-3 py-1.5 rounded-md text-sm ${
                          task.status === 'in-progress'
                            ? 'bg-primary text-primary-foreground'
                            : 'bg-secondary text-secondary-foreground hover:bg-secondary/80'
                        }`}
                        onClick={() => handleUpdateTaskStatus(task.id, 'in-progress')}
                      >
                        Start Working
                      </button>
                      <button
                        className={`px-3 py-1.5 rounded-md text-sm flex items-center ${
                          task.status === 'completed'
                            ? 'bg-green-600 text-white'
                            : 'bg-secondary text-secondary-foreground hover:bg-secondary/80'
                        }`}
                        onClick={() => handleUpdateTaskStatus(task.id, 'completed')}
                      >
                        <CheckCircle className="h-4 w-4 mr-1" />
                        Mark Complete
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </DashboardLayout>
  );
};

export default StudentDashboard;
