import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import DashboardLayout from '../components/dashboard-layout';
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import api from '../services/api';
import io from 'socket.io-client';

const Chat = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [socket, setSocket] = useState(null);
  const [loading, setLoading] = useState(true);

  // إنشاء اتصال WebSocket عند تحميل المكون
  useEffect(() => {
    const newSocket = io('/chat', {
      auth: {
        token: sessionStorage.getItem('token')
      }
    });

    newSocket.on('connect', () => {
      console.log('تم الاتصال بخادم الدردشة');
    });

    newSocket.on('message', (message) => {
      setMessages(prevMessages => [...prevMessages, message]);
    });

    newSocket.on('disconnect', () => {
      console.log('تم قطع الاتصال بخادم الدردشة');
    });

    setSocket(newSocket);

    // تنظيف الاتصال عند إزالة المكون
    return () => {
      newSocket.disconnect();
    };
  }, []);

  // جلب المستخدمين
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await api.get('/chat/contacts');
        setUsers(response.data.contacts);
        setLoading(false);
      } catch (error) {
        console.error('خطأ في جلب المستخدمين:', error);
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // جلب الرسائل عند تغيير المستخدم المحدد
  useEffect(() => {
    if (selectedStudent) {
      const fetchMessages = async () => {
        try {
          const response = await api.get(`/chat/messages/${selectedStudent}`);
          setMessages(response.data.messages);
        } catch (error) {
          console.error('خطأ في جلب الرسائل:', error);
        }
      };

      fetchMessages();
    }
  }, [selectedStudent]);

  // إرسال رسالة جديدة
  const handleSendMessage = (e) => {
    e.preventDefault();

    if (!newMessage.trim() || !selectedStudent || !socket) return;

    const messageData = {
      content: newMessage,
      recipientId: selectedStudent,
      senderId: user.id,
      timestamp: new Date().toISOString()
    };

    socket.emit('sendMessage', messageData);

    // إضافة الرسالة إلى القائمة المحلية
    setMessages(prevMessages => [...prevMessages, {
      ...messageData,
      sender: { id: user.id, name: user.name }
    }]);

    setNewMessage('');
  };

  // تنسيق التاريخ
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-SA', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // الحصول على اسم الطالب
  const getStudentName = (studentId) => {
    const student = users.find(u => u.id === studentId);
    return student ? student.name : '';
  };

  return (
    <DashboardLayout title="Chat" userRole="admin">
      <div className="h-[calc(100vh-4rem)] flex bg-white rounded-lg shadow-lg overflow-hidden">
        {/* Student List */}
        <div className="w-80 border-r border-gray-200 bg-gray-50">
          <div className="p-4 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-800">Students</h2>
            <div className="mt-2">
              <input
                type="text"
                placeholder="Search students..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>
          <div className="overflow-y-auto h-[calc(100vh-12rem)]">
            {users.map((user) => (
              <button
                key={user.id}
                className={`w-full text-left px-4 py-3 hover:bg-gray-100 transition-colors ${
                  selectedStudent === user.id ? 'bg-blue-50 border-r-4 border-blue-500' : ''
                }`}
                onClick={() => setSelectedStudent(user.id)}
              >
                <div className="flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-semibold">
                    {user.name.charAt(0).toUpperCase()}
                  </div>
                  <div className="ml-3">
                    <p className="font-medium text-gray-800">{user.name}</p>
                    <p className="text-sm text-gray-500">{user.email}</p>
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col">
          {selectedStudent ? (
            <>
              {/* Chat Header */}
              <div className="p-4 border-b border-gray-200 bg-white">
                <div className="flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-semibold">
                    {getStudentName(selectedStudent).charAt(0).toUpperCase()}
                  </div>
                  <div className="ml-3">
                    <h2 className="text-lg font-semibold text-gray-800">{getStudentName(selectedStudent)}</h2>
                    <p className="text-sm text-gray-500">Online</p>
                  </div>
                </div>
              </div>

              {/* Messages */}
              <div className="flex-1 overflow-y-auto p-4 bg-gray-50">
                <div className="space-y-4">
                  {messages.map((message, index) => (
                    <div
                      key={index}
                      className={`flex ${
                        message.sender.id === user?.id ? "justify-end" : "justify-start"
                      }`}
                    >
                      <div
                        className={`max-w-[70%] rounded-lg p-3 ${
                          message.sender.id === user?.id
                            ? "bg-blue-500 text-white"
                            : "bg-white text-gray-800 shadow-sm"
                        }`}
                      >
                        <p className="text-sm">{message.content}</p>
                        <p className={`text-xs mt-1 ${
                          message.sender.id === user?.id ? "text-blue-100" : "text-gray-500"
                        }`}>
                          {formatDate(message.timestamp)}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Message Input */}
              <div className="p-4 border-t border-gray-200 bg-white">
                <form onSubmit={handleSendMessage} className="flex space-x-2">
                  <input
                    type="text"
                    placeholder="Type a message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    className="flex-1 px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                  <button
                    type="submit"
                    className="px-6 py-2 bg-blue-500 text-white rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors"
                  >
                    Send
                  </button>
                </form>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center bg-gray-50">
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-gray-200 flex items-center justify-center">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900">Select a student to start chatting</h3>
                <p className="mt-1 text-sm text-gray-500">Choose from the list on the left to begin your conversation</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Chat;
